        {
            string Inwords = "";

            string GTotal = GrandTotal.ToString("0.00");
            string[] arr = GTotal.ToString().Split("."[0]);
            string inwords = "";
            string rupee = (arr[0]);
            string paise = "";
            if (arr.Length == 2)
            {
                if (arr[1].Length > 0 && arr[1] != "00")
                {
                    paise = (arr[1]);
                }
            }

